var map={16:!1,17:!1,188:!1,190:!1};$(".text-inside").on("keydown",function(e){if(e.keyCode in map){map[e.keyCode]=!0;if(map[16]&&map[17]&&map[190]){$(this).css("font-size",parseInt($(this).css("font-size").slice(0,-2))+1+"px");map[190]=!1}else if(map[16]&&map[17]&&map[188]){$(this).css("font-size",parseInt($(this).css("font-size").slice(0,-2))-1+"px");map[188]=!1}}}).on("keyup",function(e){if(e.keyCode in map){map[e.keyCode]=!1}})